import React, { useState } from 'react';
import { Line, Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const indiaZones = [
  { id: 'gujarat', name: 'Gujarat', type: 'earthquake', color: 'red', x: 100, y: 150 },
  { id: 'himachal', name: 'Himachal Pradesh', type: 'earthquake', color: 'red', x: 150, y: 80 },
  { id: 'assam', name: 'Assam', type: 'flood', color: 'blue', x: 300, y: 180 },
  { id: 'bihar', name: 'Bihar', type: 'flood', color: 'blue', x: 280, y: 140 },
  { id: 'odisha', name: 'Odisha', type: 'cyclone', color: 'yellow', x: 220, y: 220 },
  { id: 'andhra', name: 'Andhra Pradesh', type: 'cyclone', color: 'yellow', x: 200, y: 260 },
];

const disasterDetails = {
  gujarat: 'Earthquake 4.5 Richter Scale detected near Surat.',
  himachal: 'Earthquake 4.7 Richter Scale detected near Shimla.',
  assam: 'Flood warning in Guwahati ‚Äì Brahmaputra rising 2cm/hr.',
  bihar: 'Flood warning in Patna ‚Äì Ganges rising 3cm/hr.',
  odisha: 'Cyclone alert issued for Odisha coast.',
  andhra: 'Cyclone alert issued for Andhra Pradesh coast.',
};

const lineChartData = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June'],
  datasets: [
    {
      label: 'Floods',
      data: [2, 3, 4, 5, 3, 2],
      borderColor: 'blue',
      backgroundColor: 'rgba(0,0,255,0.3)',
    },
    {
      label: 'Earthquakes',
      data: [1, 1, 2, 2, 1, 1],
      borderColor: 'red',
      backgroundColor: 'rgba(255,0,0,0.3)',
    },
    {
      label: 'Cyclones',
      data: [0, 1, 1, 1, 0, 0],
      borderColor: 'yellow',
      backgroundColor: 'rgba(255,255,0,0.3)',
    },
  ],
};

const pieChartData = {
  labels: ['Food', 'Medical', 'Shelter'],
  datasets: [
    {
      label: 'Resource Allocation',
      data: [40, 30, 30],
      backgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
    },
  ],
};

const barChartData = {
  labels: ['Gujarat', 'Maharashtra', 'Assam'],
  datasets: [
    {
      label: 'Number of Volunteers',
      data: [120, 95, 80],
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
    },
  ],
};

const Dashboard = () => {
  const [selectedZone, setSelectedZone] = useState(null);

  const handleZoneClick = (zoneId) => {
    setSelectedZone(zoneId);
  };

  return (
    <div className="p-4 bg-white rounded shadow mb-8">
      <h2 className="text-2xl font-bold mb-4">üó∫Ô∏è Real-Time Disaster Dashboard</h2>
      <div className="flex flex-col md:flex-row gap-8">
        {/* Interactive Map */}
        <div className="relative w-full md:w-1/2 h-96 bg-gray-100 rounded">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/4/41/India_Administrative_Map.svg"
            alt="India Map"
            className="w-full h-full object-contain"
          />
          {indiaZones.map((zone) => (
            <button
              key={zone.id}
              onClick={() => handleZoneClick(zone.id)}
              style={{
                position: 'absolute',
                top: zone.y,
                left: zone.x,
                backgroundColor: zone.color,
                borderRadius: '50%',
                width: 24,
                height: 24,
                opacity: 0.7,
                border: '2px solid white',
                cursor: 'pointer',
              }}
              title={`${zone.name} - ${zone.type.charAt(0).toUpperCase() + zone.type.slice(1)} Zone`}
            />
          ))}
          {selectedZone && (
            <div className="absolute bottom-0 left-0 bg-white p-4 rounded shadow w-full md:w-3/4">
              <h3 className="font-semibold mb-2">{indiaZones.find((z) => z.id === selectedZone).name} Details</h3>
              <p>{disasterDetails[selectedZone]}</p>
              <button
                className="mt-2 text-blue-600 underline"
                onClick={() => setSelectedZone(null)}
              >
                Close
              </button>
            </div>
          )}
        </div>

        {/* Charts */}
        <div className="w-full md:w-1/2 space-y-6">
          <div>
            <h3 className="font-semibold mb-2">Monthly Disaster Occurrences</h3>
            <Line data={lineChartData} />
          </div>
          <div>
            <h3 className="font-semibold mb-2">Resource Allocation</h3>
            <Pie data={pieChartData} />
          </div>
          <div>
            <h3 className="font-semibold mb-2">Number of Volunteers by State</h3>
            <Bar data={barChartData} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
