import React, { useState } from 'react';

const VolunteerRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    skill: '',
    state: '',
  });
  const [submitted, setSubmitted] = useState(false);

  const skills = ['Medical', 'Rescue', 'Logistics'];
  const states = ['Gujarat', 'Maharashtra', 'Assam', 'Odisha', 'Andhra Pradesh', 'Bihar', 'Chennai', 'Patna', 'Bhuj'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Fake submission
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <div className="p-4 bg-white rounded shadow max-w-4xl mx-auto text-center">
        <h2 className="text-2xl font-bold mb-4">Thank You for Registering!</h2>
        <p>We appreciate your willingness to volunteer. We will contact you soon.</p>
      </div>
    );
  }

  return (
    <div className="p-4 bg-white rounded shadow max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">📝 Volunteer Registration</h2>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-md mx-auto">
        <div>
          <label className="block mb-1 font-semibold" htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            required
            value={formData.name}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded p-2"
          />
        </div>
        <div>
          <label className="block mb-1 font-semibold" htmlFor="phone">Phone Number</label>
          <input
            id="phone"
            name="phone"
            type="tel"
            required
            value={formData.phone}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded p-2"
          />
        </div>
        <div>
          <label className="block mb-1 font-semibold" htmlFor="skill">Skill</label>
          <select
            id="skill"
            name="skill"
            required
            value={formData.skill}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded p-2"
          >
            <option value="">Select a skill</option>
            {skills.map((skill) => (
              <option key={skill} value={skill}>{skill}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block mb-1 font-semibold" htmlFor="state">State</label>
          <select
            id="state"
            name="state"
            required
            value={formData.state}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded p-2"
          >
            <option value="">Select a state</option>
            {states.map((state) => (
              <option key={state} value={state}>{state}</option>
            ))}
          </select>
        </div>
        <button
          type="submit"
          className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-6 rounded shadow transition duration-300"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default VolunteerRegistration;
