import React from 'react';

const Home = () => {
  const handleViewMap = () => {
    window.open('https://www.disastermap.example.com', '_blank');
  };

  const handleVolunteer = () => {
    window.open('https://www.volunteer.example.com/signup', '_blank');
  };

  const handleDownloadGuide = () => {
    const link = document.createElement('a');
    link.href = 'https://www.safetyguide.example.com/guide.pdf';
    link.download = 'SafetyGuide.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Hero Banner */}
      <section className="relative bg-primary text-white rounded-lg overflow-hidden shadow-lg mb-12">
        <img
          src="https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1470&q=80"
          alt="Indian Disaster"
          className="w-full h-64 object-cover opacity-70"
        />
        <div className="absolute inset-0 flex flex-col justify-center items-center px-4">
          <h1 className="text-4xl md:text-5xl font-heading font-bold mb-4 text-center drop-shadow-lg">
            VigrahaRakṣitā – भारत की आपदा सुरक्षा प्रहरी
          </h1>
          <p className="text-lg md:text-xl mb-6 text-center drop-shadow-md">
            Guardian of India during Disasters
          </p>
          <div className="flex flex-wrap justify-center gap-4">
            <button
              onClick={handleViewMap}
              className="bg-secondary hover:bg-green-700 text-white font-semibold py-2 px-6 rounded shadow transition duration-300"
            >
              View Disaster Map
            </button>
            <button
              onClick={handleVolunteer}
              className="bg-secondary hover:bg-green-700 text-white font-semibold py-2 px-6 rounded shadow transition duration-300"
            >
              Volunteer Now
            </button>
            <button
              onClick={handleDownloadGuide}
              className="bg-secondary hover:bg-green-700 text-white font-semibold py-2 px-6 rounded shadow transition duration-300"
            >
              Download Safety Guide
            </button>
          </div>
        </div>
      </section>

      {/* Brief about disaster preparedness */}
      <section className="mb-12 max-w-4xl mx-auto text-center">
        <h2 className="text-3xl font-heading font-bold mb-4 text-primary">
          Importance of Disaster Preparedness in India
        </h2>
        <p className="text-lg text-gray-700">
          India faces frequent natural disasters such as floods, earthquakes, and cyclones. Being prepared can save lives and reduce damage. VigrahaRakṣitā aims to provide timely information and resources to help communities stay safe and resilient.
        </p>
      </section>
    </div>
  );
};

export default Home;
