import React, { useState } from 'react';
import { FaPhoneAlt, FaFireExtinguisher, FaExclamationTriangle, FaChevronDown, FaChevronUp } from 'react-icons/fa';

const contacts = [
  {
    name: 'NDRF (National Disaster Response Force)',
    phone: '011-12345678',
    icon: 'exclamation-triangle',
    type: 'Disaster Response',
  },
  {
    name: 'Fire Department',
    phone: '101',
    icon: 'fire-extinguisher',
    type: 'Fire Emergency',
  },
  {
    name: 'State Disaster Management Authorities (SDMAs)',
    phone: '011-87654321',
    icon: 'phone-alt',
    type: 'Disaster Management',
  },
];

const disasterDetails = [
  {
    title: 'Earthquake',
    description:
      'An earthquake is the shaking of the surface of the Earth resulting from a sudden release of energy in the Earth\'s lithosphere that creates seismic waves. Stay indoors, take cover under sturdy furniture, and avoid windows.',
  },
  {
    title: 'Flood',
    description:
      'Floods are an overflow of water that submerges land that is usually dry. Move to higher ground immediately, avoid walking or driving through floodwaters, and listen to emergency alerts.',
  },
  {
    title: 'Cyclone',
    description:
      'A cyclone is a large scale air mass that rotates around a strong center of low atmospheric pressure. Secure your home, stay indoors away from windows, and follow evacuation orders if given.',
  },
];

const iconMap = {
  'exclamation-triangle': FaExclamationTriangle,
  'fire-extinguisher': FaFireExtinguisher,
  'phone-alt': FaPhoneAlt,
};

const EmergencyContact = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const handleCall = (phone) => {
    window.location.href = `tel:${phone}`;
  };

  const handleSMS = (phone) => {
    window.location.href = `sms:${phone}`;
  };

  const toggleAccordion = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-lg max-w-6xl mx-auto my-8">
      <header className="mb-8 text-center">
        <h2 className="text-4xl font-extrabold mb-3">ðŸ†˜ Emergency Contact & Natural Disaster Info</h2>
        <p className="text-gray-700 max-w-3xl mx-auto">
          Quickly access important emergency contacts and learn essential information about common natural disasters to stay prepared.
        </p>
      </header>

      <div className="flex flex-col md:flex-row gap-10">
        {/* Contacts Section */}
        <div className="md:w-1/2 space-y-6">
          {contacts.map((contact) => {
            const IconComponent = iconMap[contact.icon];
            return (
              <div
                key={contact.phone}
                className="flex items-center justify-between p-5 border border-gray-200 rounded-lg shadow hover:shadow-lg transition-shadow bg-gray-50"
              >
                <div className="flex items-center gap-5">
                  <div className="text-4xl"><IconComponent className="text-current" /></div>
                  <div>
                    <p className="font-semibold text-xl">{contact.name}</p>
                    <p className="text-gray-600 text-lg">{contact.phone}</p>
                    <p className="text-sm text-gray-500">{contact.type}</p>
                  </div>
                </div>
                <div className="flex gap-4">
                  <button
                    onClick={() => handleCall(contact.phone)}
                    className="bg-red-600 hover:bg-red-700 text-white py-2 px-6 rounded-lg transition"
                  >
                    Call Now
                  </button>
                  <button
                    onClick={() => handleSMS(contact.phone)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white py-2 px-6 rounded-lg transition"
                  >
                    Send SMS
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {/* Disaster Info Section */}
        <div className="md:w-1/2">
          <h3 className="text-3xl font-bold mb-6">Natural Disaster Information & Safety Tips</h3>
          <div className="space-y-4">
            {disasterDetails.map((disaster, index) => (
              <div
                key={disaster.title}
                className="border border-gray-300 rounded-lg bg-white shadow-sm"
              >
                <button
                  onClick={() => toggleAccordion(index)}
                  className="w-full flex justify-between items-center p-4 text-left text-xl font-semibold focus:outline-none"
                >
                  {disaster.title}
                  {activeIndex === index ? (
                    <FaChevronUp className="text-gray-600" />
                  ) : (
                    <FaChevronDown className="text-gray-600" />
                  )}
                </button>
                {activeIndex === index && (
                  <div className="p-4 border-t border-gray-300 text-gray-700">
                    {disaster.description}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyContact;
