  return (
    <div className="p-6 space-y-8">
      {/* Add statistics cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard 
          title="Active Disasters" 
          value={metrics.activeDisasters} 
          icon="🌋" 
          color="bg-red-500" 
        />
        <StatCard 
          title="Affected People" 
          value={metrics.affectedPeople.toLocaleString()} 
          icon="👥" 
          color="bg-blue-500" 
        />
        <StatCard 
          title="Deployed Resources" 
          value={metrics.deployedResources} 
          icon="🚚" 
          color="bg-green-500" 
        />
        <StatCard 
          title="Active Volunteers" 
          value={metrics.activeVolunteers} 
          icon="🦺" 
          color="bg-yellow-500" 
        />
      </div>

      {/* Enhanced Map Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-2xl font-bold mb-4">Live Disaster Map</h2>
          <div className="h-96 rounded-lg overflow-hidden">
            <MapContainer center={[20.5937, 78.9629]} zoom={5} scrollWheelZoom={false}>
