import React, { useState } from 'react';
import { Line, Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const indiaZones = [
  { id: 'gujarat', name: 'Gujarat', type: 'earthquake', color: 'red', x: 100, y: 150 },
  { id: 'himachal', name: 'Himachal Pradesh', type: 'earthquake', color: 'red', x: 150, y: 80 },
  { id: 'assam', name: 'Assam', type: 'flood', color: 'blue', x: 300, y: 180 },
  { id: 'bihar', name: 'Bihar', type: 'flood', color: 'blue', x: 280, y: 140 },
  { id: 'odisha', name: 'Odisha', type: 'cyclone', color: 'yellow', x: 220, y: 220 },
  { id: 'andhra', name: 'Andhra Pradesh', type: 'cyclone', color: 'yellow', x: 200, y: 260 },
];

const initialDisasterDetails = {
  gujarat: 'Earthquake 4.5 Richter Scale detected near Surat.',
  himachal: 'Earthquake 4.7 Richter Scale detected near Shimla.',
  assam: 'Flood warning in Guwahati ‚Äì Brahmaputra rising 2cm/hr.',
  bihar: 'Flood warning in Patna ‚Äì Ganges rising 3cm/hr.',
  odisha: 'Cyclone alert issued for Odisha coast.',
  andhra: 'Cyclone alert issued for Andhra Pradesh coast.',
};

const generateRandomData = () => ({
  floods: Array.from({ length: 6 }, () => Math.floor(Math.random() * 6)),
  earthquakes: Array.from({ length: 6 }, () => Math.floor(Math.random() * 3)),
  cyclones: Array.from({ length: 6 }, () => Math.floor(Math.random() * 2)),
});

const Dashboard = () => {
  const [selectedZone, setSelectedZone] = useState(null);
  const [disasterDetails, setDisasterDetails] = useState(initialDisasterDetails);
  const [filters, setFilters] = useState({
    earthquake: true,
    flood: true,
    cyclone: true,
  });
  const [lineData, setLineData] = useState({
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Floods',
        data: [2, 3, 4, 5, 3, 2],
        borderColor: 'blue',
        backgroundColor: 'rgba(0,0,255,0.3)',
      },
      {
        label: 'Earthquakes',
        data: [1, 1, 2, 2, 1, 1],
        borderColor: 'red',
        backgroundColor: 'rgba(255,0,0,0.3)',
      },
      {
        label: 'Cyclones',
        data: [0, 1, 1, 1, 0, 0],
        borderColor: 'yellow',
        backgroundColor: 'rgba(255,255,0,0.3)',
      },
    ],
  });

  const pieChartData = {
    labels: ['Food', 'Medical', 'Shelter'],
    datasets: [
      {
        label: 'Resource Allocation',
        data: [40, 30, 30],
        backgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
      },
    ],
  };

  const barChartData = {
    labels: ['Gujarat', 'Maharashtra', 'Assam'],
    datasets: [
      {
        label: 'Number of Volunteers',
        data: [120, 95, 80],
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
      },
    ],
  };

  const handleZoneClick = (zoneId) => {
    setSelectedZone(zoneId);
  };

  const toggleFilter = (type) => {
    setFilters((prev) => ({ ...prev, [type]: !prev[type] }));
    setSelectedZone(null);
  };

  const refreshData = () => {
    const newData = generateRandomData();
    setLineData((prev) => ({
      ...prev,
      datasets: [
        { ...prev.datasets[0], data: newData.floods },
        { ...prev.datasets[1], data: newData.earthquakes },
        { ...prev.datasets[2], data: newData.cyclones },
      ],
    }));
  };

  return (
    <div className="p-4 bg-gradient-to-br from-blue-50 via-white to-yellow-50 rounded shadow-lg mb-8 transition-all duration-500">
      <h2 className="text-3xl font-extrabold mb-4 flex items-center gap-2">
        <span role="img" aria-label="map">üó∫Ô∏è</span> Real-Time Disaster Dashboard
      </h2>
      <div className="mb-4 flex flex-wrap gap-4">
        <button
          onClick={() => toggleFilter('earthquake')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.earthquake ? 'bg-red-500 text-white border-red-700 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-red-100'
          }`}
        >
          <span role="img" aria-label="earthquake">üåã</span> Earthquake
        </button>
        <button
          onClick={() => toggleFilter('flood')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.flood ? 'bg-blue-500 text-white border-blue-700 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-blue-100'
          }`}
        >
          <span role="img" aria-label="flood">üåä</span> Flood
        </button>
        <button
          onClick={() => toggleFilter('cyclone')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.cyclone ? 'bg-yellow-400 text-white border-yellow-600 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-yellow-100'
          }`}
        >
          <span role="img" aria-label="cyclone">üåÄ</span> Cyclone
        </button>
        <button
          onClick={refreshData}
          className="py-1.5 px-4 rounded-lg font-semibold border-2 shadow bg-green-500 text-white border-green-700 hover:bg-green-600 transition-all duration-200"
        >
          <span role="img" aria-label="refresh">üîÑ</span> Refresh Data
        </button>
      </div>
      <div className="flex flex-col md:flex-row gap-8">
        {/* Interactive Map */}
        <div className="relative w-full md:w-1/2 h-96 bg-gray-100 rounded-lg overflow-hidden shadow-inner">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/4/41/India_Administrative_Map.svg"
            alt="India Map"
            className="w-full h-full object-contain"
            style={{ filter: 'grayscale(0.2) contrast(1.1)' }}
          />
          {indiaZones
            .filter((zone) => filters[zone.type])
            .map((zone) => (
              <button
                key={zone.id}
                onClick={() => handleZoneClick(zone.id)}
                style={{
                  position: 'absolute',
                  top: zone.y,
                  left: zone.x,
                  background: `radial-gradient(circle at 60% 40%, ${zone.color} 70%, #fff0 100%)`,
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  opacity: 0.85,
                  border: '3px solid white',
                  cursor: 'pointer',
                  boxShadow: selectedZone === zone.id ? '0 0 12px 4px #3333' : '0 2px 8px 0 #0002',
                  transition: 'transform 0.2s, box-shadow 0.2s',
                  transform: selectedZone === zone.id ? 'scale(1.2)' : 'scale(1)',
                  zIndex: selectedZone === zone.id ? 20 : 10,
                }}
                title={`${zone.name} - ${zone.type.charAt(0).toUpperCase() + zone.type.slice(1)} Zone`}
                onMouseEnter={e => e.currentTarget.style.opacity = 1}
                onMouseLeave={e => e.currentTarget.style.opacity = 0.85}
              >
                <span style={{fontSize: 18, color: '#fff', textShadow: '0 1px 4px #0008'}}>
                  {zone.type === 'earthquake' ? 'üåã' : zone.type === 'flood' ? 'üåä' : 'üåÄ'}
                </span>
              </button>
            ))}
          {selectedZone && (
            <div className="absolute bottom-0 left-0 bg-white/95 p-4 rounded-t-lg shadow-lg w-full md:w-3/4 z-30 animate-fadeIn">
              <h3 className="font-bold mb-2 text-lg flex items-center gap-2">
                <span>
                  {indiaZones.find((z) => z.id === selectedZone).type === 'earthquake' ? 'üåã' :
                   indiaZones.find((z) => z.id === selectedZone).type === 'flood' ? 'üåä' : 'üåÄ'}
                </span>
                {indiaZones.find((z) => z.id === selectedZone).name} Details
              </h3>
              <p className="mb-2">{disasterDetails[selectedZone]}</p>
              <button
                className="mt-2 text-blue-600 underline hover:text-blue-900 transition"
                onClick={() => setSelectedZone(null)}
              >
                Close
              </button>
            </div>
          )}
        </div>

        {/* Charts */}
        <div className="w-full md:w-1/2 space-y-6">
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="chart">üìà</span> Monthly Disaster Occurrences
            </h3>
            <Line
              data={lineData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="pie">ü•ß</span> Resource Allocation
            </h3>
            <Pie
              data={pieChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="bar">üßë‚Äçü§ù‚Äçüßë</span> Number of Volunteers by State
            </h3>
            <Bar
              data={barChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
        </div>
      </div>
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px);}
            to { opacity: 1; transform: translateY(0);}
          }
          .animate-fadeIn {
            animation: fadeIn 0.4s cubic-bezier(.4,0,.2,1);
          }
        `}
      </style>
    </div>
  );
};

export default Dashboard;
