import React, { useState, useMemo } from 'react';
import { Line, Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix default icon issue with Leaflet in React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const indiaZones = [
  { id: 'gujarat', name: 'Gujarat', type: 'earthquake', color: 'red', lat: 21.1702, lng: 72.8311 },
  { id: 'himachal', name: 'Himachal Pradesh', type: 'earthquake', color: 'red', lat: 31.1048, lng: 77.1734 },
  { id: 'assam', name: 'Assam', type: 'flood', color: 'blue', lat: 26.2006, lng: 92.9376 },
  { id: 'bihar', name: 'Bihar', type: 'flood', color: 'blue', lat: 25.0961, lng: 85.3131 },
  { id: 'odisha', name: 'Odisha', type: 'cyclone', color: 'yellow', lat: 20.9517, lng: 85.0985 },
  { id: 'andhra', name: 'Andhra Pradesh', type: 'cyclone', color: 'yellow', lat: 15.9129, lng: 79.7400 },
];

const initialDisasterDetails = {
  gujarat: 'Earthquake 4.5 Richter Scale detected near Surat.',
  himachal: 'Earthquake 4.7 Richter Scale detected near Shimla.',
  assam: 'Flood warning in Guwahati – Brahmaputra rising 2cm/hr.',
  bihar: 'Flood warning in Patna – Ganges rising 3cm/hr.',
  odisha: 'Cyclone alert issued for Odisha coast.',
  andhra: 'Cyclone alert issued for Andhra Pradesh coast.',
};

const generateRandomData = () => ({
  floods: Array.from({ length: 6 }, () => Math.floor(Math.random() * 6)),
  earthquakes: Array.from({ length: 6 }, () => Math.floor(Math.random() * 3)),
  cyclones: Array.from({ length: 6 }, () => Math.floor(Math.random() * 2)),
});

const Dashboard = () => {
  const [selectedZone, setSelectedZone] = useState(null);
  const [disasterDetails, setDisasterDetails] = useState(initialDisasterDetails);
  const [filters, setFilters] = useState({
    earthquake: true,
    flood: true,
    cyclone: true,
  });
  const [lineData, setLineData] = useState({
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Floods',
        data: [2, 3, 4, 5, 3, 2],
        borderColor: 'blue',
        backgroundColor: 'rgba(0,0,255,0.3)',
      },
      {
        label: 'Earthquakes',
        data: [1, 1, 2, 2, 1, 1],
        borderColor: 'red',
        backgroundColor: 'rgba(255,0,0,0.3)',
      },
      {
        label: 'Cyclones',
        data: [0, 1, 1, 1, 0, 0],
        borderColor: 'yellow',
        backgroundColor: 'rgba(255,255,0,0.3)',
      },
    ],
  });

  const pieChartData = {
    labels: ['Food', 'Medical', 'Shelter'],
    datasets: [
      {
        label: 'Resource Allocation',
        data: [40, 30, 30],
        backgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
      },
    ],
  };

  const barChartData = {
    labels: ['Gujarat', 'Maharashtra', 'Assam'],
    datasets: [
      {
        label: 'Number of Volunteers',
        data: [120, 95, 80],
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
      },
    ],
  };

  const handleZoneClick = (zoneId) => {
    setSelectedZone(zoneId);
  };

  const toggleFilter = (type) => {
    setFilters((prev) => ({ ...prev, [type]: !prev[type] }));
    setSelectedZone(null);
  };

  const refreshData = () => {
    const newData = generateRandomData();
    setLineData((prev) => ({
      ...prev,
      datasets: [
        { ...prev.datasets[0], data: newData.floods },
        { ...prev.datasets[1], data: newData.earthquakes },
        { ...prev.datasets[2], data: newData.cyclones },
      ],
    }));
  };

  return (
    <div className="p-4 bg-gradient-to-br from-blue-50 via-white to-yellow-50 rounded shadow-lg mb-8 transition-all duration-500">
      <h2 className="text-3xl font-extrabold mb-4 flex items-center gap-2">
        <span role="img" aria-label="map">🗺️</span> Real-Time Disaster Dashboard
      </h2>
      <div className="mb-4 flex flex-wrap gap-4">
        <button
          onClick={() => toggleFilter('earthquake')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.earthquake ? 'bg-red-500 text-white border-red-700 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-red-100'
          }`}
        >
          <span role="img" aria-label="earthquake">🌋</span> Earthquake
        </button>
        <button
          onClick={() => toggleFilter('flood')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.flood ? 'bg-blue-500 text-white border-blue-700 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-blue-100'
          }`}
        >
          <span role="img" aria-label="flood">🌊</span> Flood
        </button>
        <button
          onClick={() => toggleFilter('cyclone')}
          className={`py-1.5 px-4 rounded-lg font-semibold border-2 shadow transition-all duration-200 ${
            filters.cyclone ? 'bg-yellow-400 text-white border-yellow-600 scale-105' : 'bg-gray-200 text-gray-700 border-gray-300 hover:bg-yellow-100'
          }`}
        >
          <span role="img" aria-label="cyclone">🌀</span> Cyclone
        </button>
        <button
          onClick={refreshData}
          className="py-1.5 px-4 rounded-lg font-semibold border-2 shadow bg-green-500 text-white border-green-700 hover:bg-green-600 transition-all duration-200"
        >
          <span role="img" aria-label="refresh">🔄</span> Refresh Data
        </button>
      </div>
      <div className="flex flex-col md:flex-row gap-8">
        {/* Dynamic Leaflet Map */}
        <div className="w-full md:w-1/2 h-96 rounded-lg overflow-hidden shadow-inner">
          <MapContainer center={[22.9734, 78.6569]} zoom={5} scrollWheelZoom={true} className="w-full h-full rounded-lg">
            <TileLayer
              attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {indiaZones
              .filter((zone) => filters[zone.type])
              .map((zone) => (
                <Marker
                  key={zone.id}
                  position={[zone.lat, zone.lng]}
                  eventHandlers={{
                    click: () => {
                      handleZoneClick(zone.id);
                    },
                  }}
                >
                  <Popup>
                    <strong>{zone.name}</strong><br />
                    {disasterDetails[zone.id]}
                  </Popup>
                </Marker>
              ))}
          </MapContainer>
          {selectedZone && (
            <div className="absolute bottom-0 left-0 bg-white/95 p-4 rounded-t-lg shadow-lg w-full md:w-3/4 z-30 animate-fadeIn">
              <h3 className="font-bold mb-2 text-lg flex items-center gap-2">
                <span>
                  {indiaZones.find((z) => z.id === selectedZone).type === 'earthquake' ? '🌋' :
                   indiaZones.find((z) => z.id === selectedZone).type === 'flood' ? '🌊' : '🌀'}
                </span>
                {indiaZones.find((z) => z.id === selectedZone).name} Details
              </h3>
              <p className="mb-2">{disasterDetails[selectedZone]}</p>
              <button
                className="mt-2 text-blue-600 underline hover:text-blue-900 transition"
                onClick={() => setSelectedZone(null)}
              >
                Close
              </button>
            </div>
          )}
        </div>

        {/* Charts */}
        <div className="w-full md:w-1/2 space-y-6">
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="chart">📈</span> Monthly Disaster Occurrences
            </h3>
            <Line
              data={lineData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="pie">🥧</span> Resource Allocation
            </h3>
            <Pie
              data={pieChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
          <div className="bg-white rounded-lg shadow p-4 transition-all duration-300 hover:shadow-lg">
            <h3 className="font-semibold mb-2 flex items-center gap-2">
              <span role="img" aria-label="bar">🧑‍🤝‍🧑</span> Number of Volunteers by State
            </h3>
            <Bar
              data={barChartData}
              options={{
                responsive: true,
                plugins: {
                  legend: { position: 'top', labels: { font: { size: 14 } } },
                  tooltip: { enabled: true, backgroundColor: '#222', titleColor: '#fff', bodyColor: '#fff' }
                },
                animation: { duration: 900, easing: 'easeOutQuart' }
              }}
            />
          </div>
        </div>
      </div>
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(30px);}
            to { opacity: 1; transform: translateY(0);}
          }
          .animate-fadeIn {
            animation: fadeIn 0.4s cubic-bezier(.4,0,.2,1);
          }
        `}
      </style>
    </div>
  );
};

export default Dashboard;
