import React, { useState } from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const ResourceManagement = () => {
  const [resources, setResources] = useState([
    { id: 1, name: 'Food Packets', quantity: 1500, category: 'Food', status: 'In Stock' },
    { id: 2, name: 'Medical Kits', quantity: 300, category: 'Medical', status: 'Low Stock' },
    { id: 3, name: 'Tents', quantity: 200, category: 'Shelter', status: 'In Stock' },
  ]);

  const chartData = {
    labels: ['Food', 'Medical', 'Shelter'],
    datasets: [
      {
        data: resources.map(r => r.quantity),
        backgroundColor: ['#4A90E2', '#D0021B', '#F5A623'],
      },
    ],
  };

  return (
    <div className="p-6 bg-white rounded shadow max-w-4xl mx-auto">
      <h2 className="text-3xl font-extrabold mb-6">ðŸ“¦ Resource Management</h2>

      <div className="mb-8">
        <div className="p-4 bg-blue-50 rounded-lg">
          <Pie data={chartData} />
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full table-auto">
          <thead>
            <tr className="bg-gray-50">
              <th className="px-6 py-3 text-left font-medium">Resource</th>
              <th className="px-6 py-3 text-left font-medium">Quantity</th>
              <th className="px-6 py-3 text-left font-medium">Category</th>
              <th className="px-6 py-3 text-left font-medium">Status</th>
            </tr>
          </thead>
          <tbody>
            {resources.map(resource => (
              <tr key={resource.id} className="border-b">
                <td className="px-6 py-4">{resource.name}</td>
                <td className="px-6 py-4">{resource.quantity}</td>
                <td className="px-6 py-4">{resource.category}</td>
                <td className="px-6 py-4">
                  <span className={`px-3 py-1 rounded-full text-sm ${
                    resource.status === 'In Stock' ? 'bg-green-100 text-green-800' : 
                    'bg-yellow-100 text-yellow-800'
                  }`}>
                    {resource.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ResourceManagement;
